  // Allow all hosts for Replit environment
  async headers() {
    return [
      {
        source: '/(.*)',
        headers: [
          {
            key: 'Cache-Control',
            value: 'no-cache, no-store, must-revalidate',
          },
          {
            key: 'X-Frame-Options',
            value: 'ALLOWALL',
          },
        ],
      },
    ];
  },
  // Configure for Replit proxy environment
  // Disable host checking for Replit iframe
  devIndicators: {
    buildActivity: false,
  },
};

module.exports = nextConfig;

