components/car-model/ProsConsSection.tsx.content:
'use client'

import { useState } from 'react'
import { ThumbsUp, ThumbsDown } from 'lucide-react'

interface ProsCon {
  id: string
  text: string
  isVisible: boolean
}

interface ProsConsData {
  pros: ProsCon[]
  cons: ProsCon[]
}

interface ProsConsSectionProps {
  carName: string
  data: ProsConsData
}

export default function ProsConsSection({ carName, data }: ProsConsSectionProps) {
  const [showAllPros, setShowAllPros] = useState(false)
  const [showAllCons, setShowAllCons] = useState(false)

  const visiblePros = showAllPros ? data.pros : data.pros.filter(pro => pro.isVisible)
  const visibleCons = showAllCons ? data.cons : data.cons.filter(con => con.isVisible)

  const hasMorePros = data.pros.length > data.pros.filter(pro => pro.isVisible).length
  const hasMoreCons = data.cons.length > data.cons.filter(con => con.isVisible).length

  return (
    <section className="py-16 bg-gray-100">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Title - matching screenshot exactly */}
        <h2 className="text-4xl font-bold text-gray-700 mb-12">
          {carName} Pros & Cons
        </h2>

        {/* Two Column Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Pros Section */}
          <div className="bg-white rounded-3xl shadow-sm border border-gray-200 p-8">
            <div className="flex items-center mb-8">
              <div className="flex items-center justify-center w-10 h-10 mr-4">
                <ThumbsUp className="w-8 h-8 text-teal-400" strokeWidth={1.5} />
              </div>
              <h3 className="text-2xl font-bold text-gray-900">Pros</h3>
            </div>

            <ul className="space-y-6">
              {visiblePros.map((pro) => (
                <li key={pro.id} className="flex items-start">
                  <div className="w-2 h-2 bg-gray-600 rounded-full mt-3 mr-4 flex-shrink-0"></div>
                  <p className="text-gray-700 text-base leading-relaxed font-normal">{pro.text}</p>
                </li>
              ))}
            </ul>

            {hasMorePros && !showAllPros && (
              <div className="flex justify-end mt-8">
                <button
                  onClick={() => setShowAllPros(true)}
                  className="text-gray-600 hover:text-gray-800 font-normal text-base transition-colors"
                >
                  ...more
                </button>
              </div>
            )}
          </div>

          {/* Cons Section */}
          <div className="bg-white rounded-3xl shadow-sm border border-gray-200 p-8">
            <div className="flex items-center mb-8">
              <div className="flex items-center justify-center w-10 h-10 mr-4">
                <ThumbsDown className="w-8 h-8 text-orange-400" strokeWidth={1.5} />
              </div>
              <h3 className="text-2xl font-bold text-gray-900">Cons</h3>
            </div>

            <ul className="space-y-6">
              {visibleCons.map((con) => (
                <li key={con.id} className="flex items-start">
                  <div className="w-2 h-2 bg-gray-600 rounded-full mt-3 mr-4 flex-shrink-0"></div>
                  <p className="text-gray-700 text-base leading-relaxed font-normal">{con.text}</p>
                </li>
              ))}
            </ul>

            {hasMoreCons && !showAllCons && (
              <div className="flex justify-end mt-8">
                <button
                  onClick={() => setShowAllCons(true)}
                  className="text-gray-600 hover:text-gray-800 font-normal text-base transition-colors"
                >
                  ...more
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </section>
  )
}

components/car-model/StickyNavigation.ts.content:
'use client'

import { useRef } from 'react'
import { ChevronLeft, ChevronRight } from 'lucide-react'

interface CarData {
  brand: string
  model: string
  fullName: string
}

interface StickyNavigationProps {
  activeSection: string
  carData: CarData
}

export default function StickyNavigation({ activeSection, carData }: StickyNavigationProps) {
  const scrollContainerRef = useRef<HTMLDivElement>(null)

  const sections = [
    { id: 'hero', label: 'Overview' },
    { id: 'pricing', label: 'Price' },
    { id: 'specifications', label: 'Specs' },
    { id: 'variants', label: 'Variants' },
    { id: 'highlights', label: 'Features' },
    { id: 'colors', label: 'Colors' },
    { id: 'pros-cons', label: 'Pros & Cons' },
    { id: 'summary', label: 'Summary' },
    { id: 'engine', label: 'Engine' },
    { id: 'mileage', label: 'Mileage' },
    { id: 'similar-cars', label: 'Similar Cars' },
    { id: 'compare', label: 'Compare' },
    { id: 'news', label: 'News' },
    { id: 'videos', label: 'Videos' },
    { id: 'faq', label: 'FAQ' },
    { id: 'reviews', label: 'Reviews' },
    { id: 'upcoming', label: 'Upcoming' },
    { id: 'new-launches', label: 'New Cars' },
    { id: 'consultation', label: 'Consult' }
  ]

  const scrollToSection = (sectionId: string) => {
    const element = document.getElementById(sectionId)
    if (element) {
      const offset = 100
      const elementPosition = element.offsetTop - offset
      window.scrollTo({
        top: elementPosition,
        behavior: 'smooth'
      })
    }
  }

  const scrollContainer = (direction: 'left' | 'right') => {
    if (scrollContainerRef.current) {
      const scrollAmount = 200
      const newScrollLeft = direction === 'left' 
        ? scrollContainerRef.current.scrollLeft - scrollAmount
        : scrollContainerRef.current.scrollLeft + scrollAmount

      scrollContainerRef.current.scrollTo({
        left: newScrollLeft,
        behavior: 'smooth'
      })
    }
  }

  return (
    <div className="sticky top-0 z-40 bg-white border-b border-gray-200 shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between py-3 sm:py-4">
          {/* Car Name - Hidden on mobile to save space */}
          <div className="hidden md:block">
            <h3 className="text-lg font-semibold text-gray-900 truncate">
              {carData.fullName}
            </h3>
          </div>

          {/* Navigation Sections */}
          <div className="flex items-center flex-1 md:flex-initial">
            {/* Left Scroll Button - Hidden on mobile */}
            <button
              onClick={() => scrollContainer('left')}
              className="hidden sm:flex items-center justify-center w-8 h-8 rounded-full bg-gray-100 hover:bg-gray-200 transition-colors mr-3 flex-shrink-0"
            >
              <ChevronLeft className="h-4 w-4 text-gray-600" />
            </button>

            {/* Scrollable Navigation */}
            <div
              ref={scrollContainerRef}
              className="flex space-x-1 sm:space-x-2 overflow-x-auto scrollbar-hide flex-1 md:flex-initial"
              style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
            >
              {sections.map((section) => (
                <button
                  key={section.id}
                  onClick={() => scrollToSection(section.id)}
                  className={`flex-shrink-0 px-3 sm:px-4 py-2 rounded-lg text-xs sm:text-sm font-medium transition-colors whitespace-nowrap ${
                    activeSection === section.id
                      ? 'bg-orange-500 text-white'
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                  }`}
                >
                  {section.label}
                </button>
              ))}
            </div>

            {/* Right Scroll Button - Hidden on mobile */}
            <button
              onClick={() => scrollContainer('right')}
              className="hidden sm:flex items-center justify-center w-8 h-8 rounded-full bg-gray-100 hover:bg-gray-200 transition-colors ml-3 flex-shrink-0"
            >
              <ChevronRight className="h-4 w-4 text-gray-600" />
            </button>
          </div>

          {/* Quick Action Button */}
          <div className="hidden lg:block ml-4">
            <button className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors">
              Get Price
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

components/car-model/VariantCitySelectio.content:
'use client'

import { useState } from 'react'
import { MapPin, Car, ChevronDown } from 'lucide-react'

interface CarData {
  brand: string
  model: string
  fullName: string
}

interface VariantCitySelectionProps {
  carData: CarData
}

export default function VariantCitySelection({ carData }: VariantCitySelectionProps) {
  const [selectedCity, setSelectedCity] = useState('New Delhi')
  const [selectedVariant, setSelectedVariant] = useState('LXI')
  const [showCityDropdown, setShowCityDropdown] = useState(false)
  const [showVariantDropdown, setShowVariantDropdown] = useState(false)

  const cities = [
    'New Delhi', 'Mumbai', 'Bangalore', 'Chennai', 'Hyderabad', 'Pune', 'Kolkata', 'Ahmedabad',
    'Jaipur', 'Lucknow', 'Kanpur', 'Nagpur', 'Indore', 'Thane', 'Bhopal', 'Visakhapatnam'
  ]

  const variants = [
    { name: 'LXI', engine: '1.2L Petrol', transmission: 'Manual', price: '‚Çπ6.19 L' },
    { name: 'VXI', engine: '1.2L Petrol', transmission: 'Manual', price: '‚Çπ6.89 L' },
    { name: 'ZXI', engine: '1.2L Petrol', transmission: 'Manual', price: '‚Çπ7.59 L' },
    { name: 'ZXI+', engine: '1.2L Petrol', transmission: 'AMT', price: '‚Çπ8.29 L' }
  ]

  const selectedVariantData = variants.find(v => v.name === selectedVariant) || variants[0]

  return (
    <div className="bg-white mb-8">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Left Box - Choose Variant */}
        <div className="relative">
          <div className="border border-gray-300 rounded-lg p-4 bg-white">
            <label className="block text-sm font-medium text-gray-700 mb-3">
              <Car className="h-4 w-4 inline mr-2" />
              Choose Variant
            </label>
            <button
              onClick={() => setShowVariantDropdown(!showVariantDropdown)}
              className="w-full p-3 border border-gray-200 rounded-lg text-left flex items-center justify-between hover:border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition-colors bg-gray-50"
            >
              <div>
                <div className="font-medium text-gray-900">{selectedVariantData.name}</div>
                <div className="text-sm text-gray-600">{selectedVariantData.engine}</div>
              </div>
              <ChevronDown className={`h-4 w-4 text-gray-500 transition-transform ${showVariantDropdown ? 'rotate-180' : ''}`} />
            </button>

            {showVariantDropdown && (
              <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg">
                {variants.map((variant) => (
                  <button
                    key={variant.name}
                    onClick={() => {
                      setSelectedVariant(variant.name)
                      setShowVariantDropdown(false)
                    }}
                    className={`w-full p-3 text-left hover:bg-gray-50 transition-colors border-b border-gray-100 last:border-b-0 ${
                      selectedVariant === variant.name ? 'bg-orange-50 text-orange-700' : ''
                    }`}
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <div className="font-medium">{variant.name}</div>
                        <div className="text-sm text-gray-600">{variant.engine} ‚Ä¢ {variant.transmission}</div>
                      </div>
                      <div className="text-sm font-medium text-gray-900">{variant.price}</div>
                    </div>
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Right Box - Choose City */}
        <div className="relative">
          <div className="border border-gray-300 rounded-lg p-4 bg-white">
            <label className="block text-sm font-medium text-gray-700 mb-3">
              <MapPin className="h-4 w-4 inline mr-2" />
              Choose City
            </label>
            <button
              onClick={() => setShowCityDropdown(!showCityDropdown)}
              className="w-full p-3 border border-gray-200 rounded-lg text-left flex items-center justify-between hover:border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition-colors bg-gray-50"
            >
              <span className="font-medium text-gray-900">{selectedCity}</span>
              <ChevronDown className={`h-4 w-4 text-gray-500 transition-transform ${showCityDropdown ? 'rotate-180' : ''}`} />
            </button>

            {showCityDropdown && (
              <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-y-auto">
                {cities.map((city) => (
                  <button
                    key={city}
                    onClick={() => {
                      setSelectedCity(city)
                      setShowCityDropdown(false)
                    }}
                    className={`w-full p-3 text-left hover:bg-gray-50 transition-colors ${
                      selectedCity === city ? 'bg-orange-50 text-orange-700' : ''
                    }`}
                  >
                    {city}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  )
}

components/car-model/VariantSection.tsx.content:
'use client'

import { useState } from 'react'
import { useRouter } from 'next/navigation'
import { Filter, Star, Fuel, Zap, Settings } from 'lucide-react'

interface CarData {
  brand: string
  model: string
  fullName: string
}

interface VariantSectionProps {
  carData: CarData
}

export default function VariantSection({ carData }: VariantSectionProps) {
  const [selectedFilter, setSelectedFilter] = useState('all')
  const router = useRouter()

  const variants = [
    {
      name: 'LXI',
      price: 619000,
      transmission: 'Manual',
      fuelType: 'Petrol',
      mileage: '23.76 kmpl',
      engine: '1.2L',
      power: '89 bhp',
      features: ['Power Steering', 'Central Locking', 'Dual Airbags', 'Manual AC'],
      rating: 4.1,
      popular: false,
      valueForMoney: true
    },
    {
      name: 'VXI',
      price: 689000,
      transmission: 'Manual',
      fuelType: 'Petrol',
      mileage: '23.76 kmpl',
      engine: '1.2L',
      power: '89 bhp',
      features: ['Power Windows', 'Music System', 'Central Locking', 'Auto AC'],
      rating: 4.3,
      popular: true,
      valueForMoney: true
    },
    {
      name: 'ZXI',
      price: 759000,
      transmission: 'Manual',
      fuelType: 'Petrol',
      mileage: '23.76 kmpl',
      engine: '1.2L',
      power: '89 bhp',
      features: ['Touchscreen', 'Alloy Wheels', 'Fog Lamps', 'Auto Climate Control'],
      rating: 4.4,
      popular: true,
      valueForMoney: false
    },
    {
      name: 'ZXI+ AMT',
      price: 829000,
      transmission: 'AMT',
      fuelType: 'Petrol',
      mileage: '22.56 kmpl',
      engine: '1.2L',
      power: '89 bhp',
      features: ['AMT', 'Push Start', 'Reverse Camera', 'Premium Interior'],
      rating: 4.5,
      popular: false,
      valueForMoney: false
    }
  ]

  const filters = [
    { id: 'all', label: 'All' },
    { id: 'petrol', label: 'Fuel Type' },
    { id: 'manual', label: 'Transmission Type' },
    { id: 'value', label: 'Value for Money Variants' }
  ]

  const filteredVariants = variants.filter(variant => {
    if (selectedFilter === 'all') return true
    if (selectedFilter === 'petrol') return variant.fuelType === 'Petrol'
    if (selectedFilter === 'manual') return variant.transmission === 'Manual'
    if (selectedFilter === 'value') return variant.valueForMoney
    return true
  })

  const handleVariantClick = (variant: typeof variants[0]) => {
    // Navigate to the new variant page
    router.push('/variant-new')
  }

  const VariantCard = ({ variant }: { variant: typeof variants[0] }) => (
    <div 
      className="bg-white border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow cursor-pointer"
      onClick={() => handleVariantClick(variant)}
    >
      <div className="flex items-start justify-between mb-3">
        <div>
          <div className="flex items-center space-x-2 mb-1">
            <h3 className="text-lg font-bold text-gray-900">{variant.name}</h3>
            {variant.popular && (
              <span className="bg-orange-500 text-white text-xs px-2 py-1 rounded-full">Popular</span>
            )}
            {variant.valueForMoney && (
              <span className="bg-green-500 text-white text-xs px-2 py-1 rounded-full">Value</span>
            )}
          </div>
          <div className="text-xl font-bold text-orange-600 mb-1">
            ‚Çπ{(variant.price / 100000).toFixed(2)} Lakh
          </div>
          <div className="text-xs text-gray-500">Ex-showroom price</div>
        </div>
        <div className="flex items-center space-x-1">
          <Star className="h-4 w-4 text-yellow-400 fill-current" />
          <span className="text-sm font-medium">{variant.rating}</span>
        </div>
      </div>

      {/* Key Highlights */}
      <div className="mb-3">
        <div className="flex flex-wrap gap-1 mb-2">
          <span className="text-xs bg-blue-100 text-blue-700 px-2 py-1 rounded">
            {variant.mileage}
          </span>
          <span className="text-xs bg-green-100 text-green-700 px-2 py-1 rounded">
            {variant.transmission}
          </span>
          <span className="text-xs bg-purple-100 text-purple-700 px-2 py-1 rounded">
            {variant.power}
          </span>
        </div>
        <p className="text-xs text-gray-600">
          Key features: {variant.features.slice(0, 3).join(', ')}
        </p>
      </div>

      {/* Action Button */}
      <button 
        className="w-full bg-orange-500 hover:bg-orange-600 text-white py-2 px-4 rounded-lg text-sm font-medium transition-colors"
        onClick={(e) => {
          e.stopPropagation() // Prevent card click when button is clicked
          handleVariantClick(variant)
        }}
      >
        View Details
      </button>
    </div>
  )

  return (
    <div className="bg-white border border-gray-200 rounded-lg p-6 mb-8">
      <h2 className="text-xl font-bold text-gray-900 mb-6">
        {carData.fullName} - Variants
      </h2>

      {/* Filters */}
      <div className="flex items-center space-x-2 mb-6">
        <Filter className="h-4 w-4 text-gray-600" />
        <div className="flex flex-wrap gap-2">
          {filters.map((filter) => (
            <button
              key={filter.id}
              onClick={() => setSelectedFilter(filter.id)}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedFilter === filter.id
                  ? 'bg-orange-500 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              {filter.label}
            </button>
          ))}
        </div>
      </div>

      {/* Variants Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        {filteredVariants.map((variant) => (
          <VariantCard key={variant.name} variant={variant} />
        ))}
      </div>

      {/* More Variants Button */}
      <div className="text-center">
        <button className="bg-white border border-orange-500 text-orange-500 hover:bg-orange-50 px-6 py-2 rounded-lg font-medium transition-colors">
          More Variants
        </button>
      </div>
    </div>
  )
}

components/cars/CarFilters.tsx.content:
'use client'

import { useState } from 'react'
import { ChevronDown, ChevronUp, Filter, X } from 'lucide-react'

export default function CarFilters() {
  const [isFiltersOpen, setIsFiltersOpen] = useState(false)
  const [expandedSections, setExpandedSections] = useState({
    brand: true,
    price: true,
    fuel: true,
    body: true,
    transmission: false,
    seating: false,
  })

  const [selectedFilters, setSelectedFilters] = useState({
    brands: [] as string[],
    priceRange: [0, 50],
    fuelTypes: [] as string[],
    bodyTypes: [] as string[],
    transmissions: [] as string[],
    seatingCapacity: [] as string[],
  })

  const toggleSection = (section: keyof typeof expandedSections) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }))
  }

  const handleFilterChange = (category: keyof typeof selectedFilters, value: any) => {
    setSelectedFilters(prev => ({
      ...prev,
      [category]: value
    }))
  }

  const brands = [
    'Maruti Suzuki', 'Hyundai', 'Tata', 'Mahindra', 'Honda', 'Toyota', 
    'Kia', 'MG', 'Renault', 'Nissan', 'Ford', 'Volkswagen'
  ]

  const fuelTypes = ['Petrol', 'Diesel', 'CNG', 'Electric', 'Hybrid']
  const bodyTypes = ['Hatchback', 'Sedan', 'SUV', 'Compact SUV', 'MPV', 'Coupe', 'Convertible']
  const transmissions = ['Manual', 'Automatic', 'CVT', 'AMT']
  const seatingOptions = ['2', '4', '5', '6', '7', '8+']

  const FilterSection = ({ 
    title, 
    isExpanded, 
    onToggle, 
    children 
  }: { 
    title: string
    isExpanded: boolean
    onToggle: () => void
    children: React.ReactNode 
  }) => (
    <div className="border-b border-gray-200 pb-4 mb-4">
      <button
        onClick={onToggle}
        className="flex items-center justify-between w-full text-left font-medium text-gray-900 hover:text-primary-600"
      >
        <span>{title}</span>
        {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
      </button>
      {isExpanded && <div className="mt-3">{children}</div>}
    </div>
  )

  return (
    <>
      {/* Mobile Filter Button */}
      <div className="lg:hidden mb-4">
        <button
          onClick={() => setIsFiltersOpen(true)}
          className="w-full bg-white border border-gray-300 rounded-lg px-4 py-2 flex items-center justify-center space-x-2 hover:bg-gray-50"
        >
          <Filter className="h-5 w-5" />
          <span>Filters</span>
        </button>
      </div>

      {/* Mobile Filter Modal */}
      {isFiltersOpen && (
        <div className="fixed inset-0 z-50 lg:hidden">
          <div className="fixed inset-0 bg-black bg-opacity-50" onClick={() => setIsFiltersOpen(false)} />
          <div className="fixed inset-y-0 left-0 w-full max-w-sm bg-white shadow-xl">
            <div className="flex items-center justify-between p-4 border-b border-gray-200">
              <h2 className="text-lg font-semibold">Filters</h2>
              <button onClick={() => setIsFiltersOpen(false)}>
                <X className="h-6 w-6" />
              </button>
            </div>
            <div className="p-4 overflow-y-auto h-full pb-20">
              <FiltersContent />
            </div>
            <div className="absolute bottom-0 left-0 right-0 p-4 bg-white border-t border-gray-200">
              <button
                onClick={() => setIsFiltersOpen(false)}
                className="w-full bg-primary-600 text-white py-2 px-4 rounded-lg font-medium"
              >
                Apply Filters
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Desktop Filters */}
      <div className="hidden lg:block bg-white rounded-lg border border-gray-200 p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-lg font-semibold text-gray-900">Filters</h2>
          <button className="text-sm text-primary-600 hover:text-primary-700">
            Clear All
          </button>
        </div>
        <FiltersContent />
      </div>
    </>
  )

  function FiltersContent() {
    return (
      <div className="space-y-6">
        {/* Brand Filter */}
        <FilterSection
          title="Brand"
          isExpanded={expandedSections.brand}
          onToggle={() => toggleSection('brand')}
        >
          <div className="space-y-2 max-h-48 overflow-y-auto">
            {brands.map((brand) => (
              <label key={brand} className="flex items-center">
                <input
                  type="checkbox"
                  className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  checked={selectedFilters.brands.includes(brand)}
                  onChange={(e) => {
                    const newBrands = e.target.checked
                      ? [...selectedFilters.brands, brand]
                      : selectedFilters.brands.filter(b => b !== brand)
                    handleFilterChange('brands', newBrands)
                  }}
                />
                <span className="ml-2 text-sm text-gray-700">{brand}</span>
              </label>
            ))}
          </div>
        </FilterSection>

        {/* Price Range Filter */}
        <FilterSection
          title="Price Range"
          isExpanded={expandedSections.price}
          onToggle={() => toggleSection('price')}
        >
          <div className="space-y-4">
            <div className="flex items-center space-x-4">
              <input
                type="number"
                placeholder="Min"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm"
                value={selectedFilters.priceRange[0] || ''}
                onChange={(e) => handleFilterChange('priceRange', [Number(e.target.value), selectedFilters.priceRange[1]])}
              />
              <span className="text-gray-500">to</span>
              <input
                type="number"
                placeholder="Max"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm"
                value={selectedFilters.priceRange[1] || ''}
                onChange={(e) => handleFilterChange('priceRange', [selectedFilters.priceRange[0], Number(e.target.value)])}
              />
            </div>
            <div className="grid grid-cols-2 gap-2 text-xs">
              {['Under 5L', '5-10L', '10-15L', '15-25L', '25L+'].map((range) => (
                <button
                  key={range}
                  className="px-3 py-1 border border-gray-300 rounded-full hover:border-primary-500 hover:text-primary-600"
                >
                  {range}
                </button>
              ))}
            </div>
          </div>
        </FilterSection>

        {/* Fuel Type Filter */}
        <FilterSection
          title="Fuel Type"
          isExpanded={expandedSections.fuel}
          onToggle={() => toggleSection('fuel')}
        >
          <div className="space-y-2">
            {fuelTypes.map((fuel) => (
              <label key={fuel} className="flex items-center">
                <input
                  type="checkbox"
                  className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  checked={selectedFilters.fuelTypes.includes(fuel)}
                  onChange={(e) => {
                    const newFuels = e.target.checked
                      ? [...selectedFilters.fuelTypes, fuel]
                      : selectedFilters.fuelTypes.filter(f => f !== fuel)
                    handleFilterChange('fuelTypes', newFuels)
                  }}
                />
                <span className="ml-2 text-sm text-gray-700">{fuel}</span>
              </label>
            ))}
          </div>
        </FilterSection>

        {/* Body Type Filter */}
        <FilterSection
          title="Body Type"
          isExpanded={expandedSections.body}
          onToggle={() => toggleSection('body')}
        >
          <div className="space-y-2">
            {bodyTypes.map((body) => (
              <label key={body} className="flex items-center">
                <input
                  type="checkbox"
                  className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  checked={selectedFilters.bodyTypes.includes(body)}
                  onChange={(e) => {
                    const newBodies = e.target.checked
                      ? [...selectedFilters.bodyTypes, body]
                      : selectedFilters.bodyTypes.filter(b => b !== body)
                    handleFilterChange('bodyTypes', newBodies)
                  }}
                />
                <span className="ml-2 text-sm text-gray-700">{body}</span>
              </label>
            ))}
          </div>
        </FilterSection>

        {/* Transmission Filter */}
        <FilterSection
          title="Transmission"
          isExpanded={expandedSections.transmission}
          onToggle={() => toggleSection('transmission')}
        >
          <div className="space-y-2">
            {transmissions.map((transmission) => (
              <label key={transmission} className="flex items-center">
                <input
                  type="checkbox"
                  className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  checked={selectedFilters.transmissions.includes(transmission)}
                  onChange={(e) => {
                    const newTransmissions = e.target.checked
                      ? [...selectedFilters.transmissions, transmission]
                      : selectedFilters.transmissions.filter(t => t !== transmission)
                    handleFilterChange('transmissions', newTransmissions)
                  }}
                />
                <span className="ml-2 text-sm text-gray-700">{transmission}</span>
              </label>
            ))}
          </div>
        </FilterSection>

        {/* Seating Capacity Filter */}
        <FilterSection
          title="Seating Capacity"
          isExpanded={expandedSections.seating}
          onToggle={() => toggleSection('seating')}
        >
          <div className="grid grid-cols-3 gap-2">
            {seatingOptions.map((seats) => (
              <button
                key={seats}
                className={`px-3 py-2 text-sm border rounded-lg ${
                  selectedFilters.seatingCapacity.includes(seats)
                    ? 'border-primary-500 bg-primary-50 text-primary-700'
                    : 'border-gray-300 hover:border-primary-300'
                }`}
                onClick={() => {
                  const newSeating = selectedFilters.seatingCapacity.includes(seats)
                    ? selectedFilters.seatingCapacity.filter(s => s !== seats)
                    : [...selectedFilters.seatingCapacity, seats]
                  handleFilterChange('seatingCapacity', newSeating)
                }}
              >
                {seats} Seater
              </button>
            ))}
          </div>
        </FilterSection>
      </div>
    )
  }
}

components/cars/SortOptions.tsx.content:
'use client'

import { useState } from 'react'
import { ChevronDown, Grid, List } from 'lucide-react'

export default function SortOptions() {
  const [sortBy, setSortBy] = useState('popularity')
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid')
  const [isDropdownOpen, setIsDropdownOpen] = useState(false)

  const sortOptions = [
    { value: 'popularity', label: 'Popularity' },
    { value: 'price-low-high', label: 'Price: Low to High' },
    { value: 'price-high-low', label: 'Price: High to Low' },
    { value: 'rating', label: 'Customer Rating' },
    { value: 'fuel-efficiency', label: 'Fuel Efficiency' },
    { value: 'launch-date', label: 'Launch Date' },
    { value: 'alphabetical', label: 'Name (A-Z)' },
  ]

  const selectedOption = sortOptions.find(option => option.value === sortBy)

  return (
    <div className="bg-white border border-gray-200 rounded-lg p-4 mb-6">
      <div className="flex items-center justify-between">
        {/* Sort Dropdown */}
        <div className="relative">
          <button
            onClick={() => setIsDropdownOpen(!isDropdownOpen)}
            className="flex items-center space-x-2 px-4 py-2 border border-gray-300 rounded-lg hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
          >
            <span className="text-sm font-medium text-gray-700">
              Sort by: {selectedOption?.label}
            </span>
            <ChevronDown className={`h-4 w-4 text-gray-500 transition-transform ${isDropdownOpen ? 'rotate-180' : ''}`} />
          </button>

          {isDropdownOpen && (
            <div className="absolute top-full left-0 mt-1 w-56 bg-white border border-gray-200 rounded-lg shadow-lg z-10">
              <div className="py-1">
                {sortOptions.map((option) => (
                  <button
                    key={option.value}
                    onClick={() => {
                      setSortBy(option.value)
                      setIsDropdownOpen(false)
                    }}
                    className={`w-full text-left px-4 py-2 text-sm hover:bg-gray-50 ${
                      sortBy === option.value 
                        ? 'bg-primary-50 text-primary-700 font-medium' 
                        : 'text-gray-700'
                    }`}
                  >
                    {option.label}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* View Mode Toggle */}
        <div className="hidden md:flex items-center space-x-2">
          <span className="text-sm text-gray-600">View:</span>
          <div className="flex border border-gray-300 rounded-lg overflow-hidden">
            <button
              onClick={() => setViewMode('grid')}
              className={`p-2 ${
                viewMode === 'grid'
                  ? 'bg-primary-600 text-white'
                  : 'bg-white text-gray-600 hover:bg-gray-50'
              }`}
            >
              <Grid className="h-4 w-4" />
            </button>
            <button
              onClick={() => setViewMode('list')}
              className={`p-2 ${
                viewMode === 'list'
                  ? 'bg-primary-600 text-white'
                  : 'bg-white text-gray-600 hover:bg-gray-50'
              }`}
            >
              <List className="h-4 w-4" />
            </button>
          </div>
        </div>
      </div>

      {/* Results Count */}
      <div className="mt-3 pt-3 border-t border-gray-200">
        <p className="text-sm text-gray-600">
          Showing <span className="font-medium text-gray-900">1-24</span> of{' '}
          <span className="font-medium text-gray-900">487</span> cars
        </p>
      </div>
    </div>
  )
}

components/common/CarGrid.tsx.content:
'use client'

import { useState } from 'react'
import CarProductBox from './CarProductBox'
import { ChevronLeft, ChevronRight, Grid, List } from 'lucide-react'

interface CarData {
  id: number
  brand: string
  model: string
  fullName: string
  image: string
  priceRange: string
  startingPrice: number
  rating: number
  reviewCount: number
  mileage: string
  seating: number
  fuelType: string
  transmission: string
  keyFeatures: string[]
  badge?: string
  isNew?: boolean
  isPopular?: boolean
}

interface CarGridProps {
  cars: CarData[]
  title?: string
  showPagination?: boolean
  itemsPerPage?: number
  gridCols?: 2 | 3 | 4
  showViewToggle?: boolean
  className?: string
}

export default function CarGrid({
  cars,
  title,
  showPagination = false,
  itemsPerPage = 12,
  gridCols = 3,
  showViewToggle = false,
  className = ''
}: CarGridProps) {
  const [currentPage, setCurrentPage] = useState(1)
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid')

  const totalPages = Math.ceil(cars.length / itemsPerPage)
  const startIndex = (currentPage - 1) * itemsPerPage
  const endIndex = startIndex + itemsPerPage
  const currentCars = showPagination ? cars.slice(startIndex, endIndex) : cars

  const gridColsClass = {
    2: 'grid-cols-1 md:grid-cols-2',
    3: 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3',
    4: 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4'
  }

  const handlePageChange = (page: number) => {
    setCurrentPage(page)
    // Scroll to top of grid
    window.scrollTo({ top: 0, behavior: 'smooth' })
  }

  return (
    <div className={`${className}`}>
      {/* Header */}
      {(title || showViewToggle) && (
        <div className="flex items-center justify-between mb-6">
          {title && (
            <h2 className="text-2xl font-bold text-gray-900">{title}</h2>
          )}

          {showViewToggle && (
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setViewMode('grid')}
                className={`p-2 rounded-lg transition-colors ${
                  viewMode === 'grid'
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                <Grid className="h-4 w-4" />
              </button>
              <button
                onClick={() => setViewMode('list')}
                className={`p-2 rounded-lg transition-colors ${
                  viewMode === 'list'
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                <List className="h-4 w-4" />
              </button>
            </div>
          )}
        </div>
      )}

      {/* Cars Grid/List */}
      {viewMode === 'grid' ? (
        <div className={`grid gap-6 ${gridColsClass[gridCols]}`}>
          {currentCars.map((car) => (
            <CarProductBox
              key={car.id}
              car={car}
              size="medium"
              showCompare={true}
              showWishlist={true}
            />
          ))}
        </div>
      ) : (
        <div className="space-y-4">
          {currentCars.map((car) => (
            <CarProductBox
              key={car.id}
              car={car}
              size="large"
              showCompare={true}
              showWishlist={true}
              className="w-full"
            />
          ))}
        </div>
      )}

      {/* Empty State */}
      {cars.length === 0 && (
        <div className="text-center py-12">
          <div className="w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <Grid className="h-8 w-8 text-gray-400" />
          </div>
          <h3 className="text-lg font-semibold text-gray-900 mb-2">No cars found</h3>
          <p className="text-gray-600">Try adjusting your filters or search criteria.</p>
        </div>
      )}

      {/* Pagination */}
      {showPagination && totalPages > 1 && (
        <div className="flex items-center justify-center space-x-2 mt-8">
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 1}
            className="p-2 rounded-lg border border-gray-300 text-gray-600 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <ChevronLeft className="h-4 w-4" />
          </button>

          {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => {
            // Show first page, last page, current page, and pages around current
            const showPage = 
              page === 1 || 
              page === totalPages || 
              (page >= currentPage - 1 && page <= currentPage + 1)

            if (!showPage) {
              // Show ellipsis for gaps
              if (page === currentPage - 2 || page === currentPage + 2) {
                return (
                  <span key={page} className="px-2 text-gray-400">
                    ...
                  </span>
                )
              }
              return null
            }

            return (
              <button
                key={page}
                onClick={() => handlePageChange(page)}
                className={`px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                  currentPage === page
                    ? 'bg-blue-600 text-white'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                {page}
              </button>
            )
          })}

          <button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage === totalPages}
            className="p-2 rounded-lg border border-gray-300 text-gray-600 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <ChevronRight className="h-4 w-4" />
          </button>
        </div>
      )}

      {/* Results Info */}
      {showPagination && (
        <div className="text-center mt-4 text-sm text-gray-600">
          Showing {startIndex + 1}-{Math.min(endIndex, cars.length)} of {cars.length} cars
        </div>
      )}
    </div>
  )
}

components/common/CarProductBox.tsx.content:
'use client'

import { useState } from 'react'
import Link from 'next/link'
import { Star, Heart, Fuel, Users, Zap, ArrowRight } from 'lucide-react'

interface CarData {
  id: number
  brand: string
  model: string
  fullName: string
  image: string
  priceRange: string
  startingPrice: number
  rating: number
  reviewCount: number
  mileage: string
  seating: number
  fuelType: string
  transmission: string
  keyFeatures: string[]
  badge?: string
  isNew?: boolean
  isPopular?: boolean
}

interface CarProductBoxProps {
  car: CarData
  size?: 'small' | 'medium' | 'large'
  showCompare?: boolean
  showWishlist?: boolean
  className?: string
}

export default function CarProductBox({ 
  car, 
  size = 'medium', 
  showCompare = true, 
  showWishlist = true,
  className = ''
}: CarProductBoxProps) {
  const [isWishlisted, setIsWishlisted] = useState(false)

  // Generate URL-friendly slug from brand and model
  const generateSlug = (text: string) => {
    return text.toLowerCase().replace(/\s+/g, '-').replace(/[^a-z0-9-]/g, '')
  }

  const modelPageUrl = `/cars/${generateSlug(car.brand)}/${generateSlug(car.model)}`

  const toggleWishlist = (e: React.MouseEvent) => {
    e.preventDefault()
    e.stopPropagation()
    setIsWishlisted(!isWishlisted)
  }

  const handleCompare = (e: React.MouseEvent) => {
    e.preventDefault()
    e.stopPropagation()
    // Add to compare functionality
    console.log('Add to compare:', car.fullName)
  }

  const sizeClasses = {
    small: 'w-full max-w-sm',
    medium: 'w-full max-w-md',
    large: 'w-full max-w-lg'
  }

  const badgeColors = {
    'New Launch': 'bg-green-100 text-green-700',
    'Popular': 'bg-blue-100 text-blue-700',
    'Best Seller': 'bg-orange-100 text-orange-700',
    'Premium': 'bg-purple-100 text-purple-700',
    'Value': 'bg-yellow-100 text-yellow-700'
  }

  return (
    <Link href={modelPageUrl} className={`block ${sizeClasses[size]} ${className}`}>
      <div className="bg-white border border-gray-200 rounded-lg hover:shadow-lg transition-all duration-300 hover:border-blue-300 group">
        {/* Car Image */}
        <div className="relative overflow-hidden rounded-t-lg">
          <img
            src={car.image}
            alt={car.fullName}
            className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
          />

          {/* Badges */}
          <div className="absolute top-3 left-3 flex flex-col space-y-2">
            {car.isNew && (
              <span className="px-2 py-1 bg-green-500 text-white text-xs font-medium rounded">
                NEW
              </span>
            )}
            {car.isPopular && (
              <span className="px-2 py-1 bg-blue-500 text-white text-xs font-medium rounded">
                POPULAR
              </span>
            )}
            {car.badge && (
              <span className={`px-2 py-1 text-xs font-medium rounded ${badgeColors[car.badge as keyof typeof badgeColors] || 'bg-gray-100 text-gray-700'}`}>
                {car.badge}
              </span>
            )}
          </div>

          {/* Wishlist Button */}
          {showWishlist && (
            <button
              onClick={toggleWishlist}
              className={`absolute top-3 right-3 p-2 rounded-full transition-all ${
                isWishlisted
                  ? 'bg-red-500 text-white'
                  : 'bg-white text-gray-600 hover:bg-gray-100'
              }`}
            >
              <Heart className={`h-4 w-4 ${isWishlisted ? 'fill-current' : ''}`} />
            </button>
          )}

          {/* Hover Overlay */}
          <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-10 transition-all duration-300 flex items-center justify-center">
            <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-300">
              <div className="bg-white rounded-full p-3 shadow-lg">
                <ArrowRight className="h-5 w-5 text-blue-600" />
              </div>
            </div>
          </div>
        </div>

        {/* Car Details */}
        <div className="p-4">
          {/* Brand and Model */}
          <div className="flex items-center justify-between mb-2">
            <h3 className="text-lg font-bold text-gray-900 group-hover:text-blue-600 transition-colors">
              {car.fullName}
            </h3>
            <div className="flex items-center space-x-1">
              <Star className="h-4 w-4 text-yellow-400 fill-current" />
              <span className="text-sm font-medium text-gray-700">{car.rating}</span>
              <span className="text-xs text-gray-500">({car.reviewCount})</span>
            </div>
          </div>

          {/* Price */}
          <div className="mb-3">
            <p className="text-xl font-bold text-blue-600">{car.priceRange}</p>
            <p className="text-xs text-gray-500">Ex-showroom price</p>
          </div>

          {/* Key Specs */}
          <div className="grid grid-cols-3 gap-2 mb-4">
            <div className="flex items-center space-x-1">
              <Fuel className="h-4 w-4 text-green-600" />
              <span className="text-xs text-gray-600">{car.mileage}</span>
            </div>
            <div className="flex items-center space-x-1">
              <Users className="h-4 w-4 text-blue-600" />
              <span className="text-xs text-gray-600">{car.seating} Seater</span>
            </div>
            <div className="flex items-center space-x-1">
              <Zap className="h-4 w-4 text-orange-600" />
              <span className="text-xs text-gray-600">{car.fuelType}</span>
            </div>
          </div>

          {/* Key Features */}
          <div className="mb-4">
            <div className="flex flex-wrap gap-1">
              {car.keyFeatures.slice(0, 2).map((feature, index) => (
                <span key={index} className="px-2 py-1 bg-gray-100 text-xs text-gray-700 rounded">
                  {feature}
                </span>
              ))}
              {car.keyFeatures.length > 2 && (
                <span className="px-2 py-1 bg-gray-100 text-xs text-gray-700 rounded">
                  +{car.keyFeatures.length - 2} more
                </span>
              )}
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex space-x-2">
            <div className="flex-1 bg-blue-600 text-white py-2 px-3 rounded text-sm font-medium hover:bg-blue-700 transition-colors text-center group-hover:bg-blue-700">
              View Details
            </div>
            {showCompare && (
              <button
                onClick={handleCompare}
                className="px-3 py-2 border border-gray-300 text-gray-700 rounded text-sm font-medium hover:bg-gray-50 transition-colors"
              >
                Compare
              </button>
            )}
          </div>
        </div>
      </div>
    </Link>
  )
}

components/common/CitySelector.tsx.content:
'use client'

import { useState } from 'react'
import { Search, MapPin, X } from 'lucide-react'

interface City {
  id: string
  name: string
  state: string
  icon: string
}

interface CitySelectorProps {
  isOpen: boolean
  onClose: () => void
  selectedCity: string
  onCitySelect: (city: string) => void
}

export default function CitySelector({ isOpen, onClose, selectedCity, onCitySelect }: CitySelectorProps) {
  const [searchQuery, setSearchQuery] = useState('')

  const popularCities: City[] = [
    { id: 'mumbai', name: 'Mumbai', state: 'Maharashtra', icon: 'üèôÔ∏è' },
    { id: 'bangalore', name: 'Bangalore', state: 'Karnataka', icon: 'üåÜ' },
    { id: 'delhi', name: 'Delhi', state: 'Delhi', icon: 'üèõÔ∏è' },
    { id: 'pune', name: 'Pune', state: 'Maharashtra', icon: 'üè¢' },
    { id: 'hyderabad', name: 'Hyderabad', state: 'Telangana', icon: 'üïå' },
    { id: 'ahmedabad', name: 'Ahmedabad', state: 'Gujarat', icon: 'üèòÔ∏è' },
    { id: 'chennai', name: 'Chennai', state: 'Tamil Nadu', icon: 'üèñÔ∏è' },
    { id: 'kolkata', name: 'Kolkata', state: 'West Bengal', icon: 'üåâ' }
  ]

  const allCities = [
    ...popularCities,
    { id: 'jaipur', name: 'Jaipur', state: 'Rajasthan', icon: 'üè∞' },
    { id: 'lucknow', name: 'Lucknow', state: 'Uttar Pradesh', icon: 'üïå' },
    { id: 'kanpur', name: 'Kanpur', state: 'Uttar Pradesh', icon: 'üè≠' },
    { id: 'nagpur', name: 'Nagpur', state: 'Maharashtra', icon: 'üå≥' },
    { id: 'indore', name: 'Indore', state: 'Madhya Pradesh', icon: 'üèõÔ∏è' },
    { id: 'thane', name: 'Thane', state: 'Maharashtra', icon: 'üèòÔ∏è' },
    { id: 'bhopal', name: 'Bhopal', state: 'Madhya Pradesh', icon: 'üåä' },
    { id: 'visakhapatnam', name: 'Visakhapatnam', state: 'Andhra Pradesh', icon: '‚õµ' },
    { id: 'pimpri', name: 'Pimpri-Chinchwad', state: 'Maharashtra', icon: 'üè¢' },
    { id: 'patna', name: 'Patna', state: 'Bihar', icon: 'üèõÔ∏è' },
    { id: 'vadodara', name: 'Vadodara', state: 'Gujarat', icon: 'üèòÔ∏è' },
    { id: 'ghaziabad', name: 'Ghaziabad', state: 'Uttar Pradesh', icon: 'üèôÔ∏è' },
    { id: 'ludhiana', name: 'Ludhiana', state: 'Punjab', icon: 'üåæ' },
    { id: 'agra', name: 'Agra', state: 'Uttar Pradesh', icon: 'üïå' },
    { id: 'nashik', name: 'Nashik', state: 'Maharashtra', icon: 'üçá' },
    { id: 'faridabad', name: 'Faridabad', state: 'Haryana', icon: 'üè≠' },
    { id: 'meerut', name: 'Meerut', state: 'Uttar Pradesh', icon: 'üèòÔ∏è' },
    { id: 'rajkot', name: 'Rajkot', state: 'Gujarat', icon: 'üèõÔ∏è' },
    { id: 'kalyan', name: 'Kalyan-Dombivali', state: 'Maharashtra', icon: 'üèòÔ∏è' },
    { id: 'vasai', name: 'Vasai-Virar', state: 'Maharashtra', icon: 'üèñÔ∏è' }
  ]

  const filteredCities = allCities.filter(city => 
    city.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    city.state.toLowerCase().includes(searchQuery.toLowerCase())
  )

  const handleCitySelect = (cityName: string) => {
    onCitySelect(cityName)
    onClose()
  }

  const handleDetectLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          // In a real app, you'd reverse geocode these coordinates
          // For now, we'll just select Mumbai as a fallback
          handleCitySelect('Mumbai')
        },
        (error) => {
          console.error('Error getting location:', error)
          // Fallback to Mumbai
          handleCitySelect('Mumbai')
        }
      )
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl w-full max-w-md max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-100">
          <h2 className="text-xl font-bold text-gray-900">Select your City</h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
          >
            <X className="h-5 w-5 text-gray-500" />
          </button>
        </div>

        <div className="p-6 overflow-y-auto max-h-[calc(90vh-120px)]">
          {/* Search Input */}
          <div className="relative mb-6">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="Type your Pincode or City"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>

          {/* Detect Location */}
          <button
            onClick={handleDetectLocation}
            className="flex items-center space-x-2 text-blue-600 hover:text-blue-700 mb-6 font-medium"
          >
            <MapPin className="h-5 w-5" />
            <span>Detect my location</span>
          </button>

          {/* Popular Cities */}
          <div className="mb-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Popular Cities</h3>
            <div className="grid grid-cols-2 gap-4">
              {(searchQuery ? filteredCities.slice(0, 8) : popularCities).map((city) => (
                <button
                  key={city.id}
                  onClick={() => handleCitySelect(city.name)}
                  className="flex flex-col items-center p-4 rounded-xl border border-gray-100 hover:border-blue-200 hover:bg-blue-50 transition-all duration-200 group"
                >
                  <div className="w-12 h-12 bg-gradient-to-br from-blue-100 to-blue-200 rounded-full flex items-center justify-center mb-2 group-hover:from-blue-200 group-hover:to-blue-300 transition-all duration-200">
                    <span className="text-2xl">{city.icon}</span>
                  </div>
                  <span className="font-medium text-gray-900 text-sm">{city.name}</span>
                  <span className="text-xs text-gray-500">{city.state}</span>
                </button>
              ))}
            </div>
          </div>

          {/* All Cities List (when searching) */}
          {searchQuery && filteredCities.length > 8 && (
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-4">All Cities</h3>
              <div className="space-y-2">
                {filteredCities.slice(8).map((city) => (
                  <button
                    key={city.id}
                    onClick={() => handleCitySelect(city.name)}
                    className="w-full flex items-center space-x-3 p-3 rounded-lg hover:bg-gray-50 transition-colors text-left"
                  >
                    <div className="w-8 h-8 bg-gradient-to-br from-gray-100 to-gray-200 rounded-full flex items-center justify-center">
                      <span className="text-sm">{city.icon}</span>
                    </div>
                    <div>
                      <div className="font-medium text-gray-900">{city.name}</div>
                      <div className="text-sm text-gray-500">{city.state}</div>
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}

          {searchQuery && filteredCities.length === 0 && (
            <div className="text-center py-8">
              <p className="text-gray-500">No cities found matching "{searchQuery}"</p>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

